export default {
  mounted() {
    this.basicData.labels = this.Last7Days();
  },

  methods: {
    Last7Days() {
      var result = [];

      for (var i = 0; i < 7; i++) {
        var d = new Date();
        d.setDate(d.getDate() - i);
        result.add(d.toLocaleDateString('en-us', {
          weekday: "long"
        }).slice(0, 3));
      }

      return result;
    }

  },

  data() {
    return {
      basicData: {
        labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
        datasets: [{
          backgroundColor: "#277BC0",
          data: [2100, 2300, 2000, 1900, 2400, 1800, 2500]
        }]
      }
    };
  }

};
