export default {
  mounted() {
    this.labels = this.Last7Days();
  },

  methods: {
    Last7Days() {
      var result = [];

      for (var i = 0; i < 7; i++) {
        var d = new Date();
        d.setDate(d.getDate() - i);
        result.push(d.toLocaleDateString('en-us', {
          weekday: "long"
        }));
      }

      return result;
    }

  },

